import { createContext, useState, useRef, ReactNode } from "react"

type AppContextProps = {
  setError: (error: string) => void
  cache?: React.MutableRefObject<Map<string, string>>
  validateCache?: boolean
  setValidateCache?: (value: boolean) => void // Replace toggleValidateCache with setValidateCache
}

export const AppContext = createContext<AppContextProps>({ setError: () => {} })

export function AppContextProvider({ children }: { children: ReactNode }) {
  const [validateCache, setValidateCache] = useState(false)

  const contextValue = {
    setError: () => {},
    cache: useRef(new Map<string, string>()),
    validateCache,
    setValidateCache, // Include setValidateCache in the context value
  }

}
