import { createContext, useRef } from "react"

export const AppContext = createContext<AppContextProps>({ setError: () => {}, validateCache: useRef(false) })

type AppContextProps = {
  setError: (error: string) => void
  cache?: React.MutableRefObject<Map<string, string>>
  validateCache?: React.MutableRefObject<boolean>
}
